import "./InputBox.css"

function InputBox(props){
    return(
        <div className="input-box">
           <input type="text" name="input" onChange={props.onChangeHandler}/>
           {props.buttonStatus ? <button className="add-btn" onClick={props.addHandler}>ADD</button>:<button className="add-btn" onClick={props.addHandler}>Edit</button>}
        </div>
    )
}

export default InputBox
